

module AbsZp where

-- Haskell module generated by the BNF converter




newtype Ident = Ident String deriving (Eq, Ord, Show, Read)
data Program = Prog [Function]
  deriving (Eq, Ord, Show, Read)

data Function = Fun Type Ident [Decl] [Stm]
  deriving (Eq, Ord, Show, Read)

data Decl = Dec Type [Ident]
  deriving (Eq, Ord, Show, Read)

data Stm
    = SDecl Decl
    | SExp Exp
    | SBlock [Stm]
    | SWhile Exp Stm
    | SRepeat Stm Exp
    | SIf Exp Stm
    | SIfThenElse Exp Stm Stm
    | SIfThen Exp Stm
    | SFor Exp Exp Exp Stm
    | SReturn Exp
  deriving (Eq, Ord, Show, Read)

data Exp
    = EAss Ident Exp
    | ELt Exp Exp
    | EGt Exp Exp
    | EAdd Exp Exp
    | ESub Exp Exp
    | EMul Exp Exp
    | Call Ident [Exp]
    | EVar Ident
    | EStr String
    | EInt Integer
    | EDouble Double
  deriving (Eq, Ord, Show, Read)

data Type = TInt | TDouble
  deriving (Eq, Ord, Show, Read)

